-- 1. Atualizar saldo do cliente
UPDATE customers SET balance = balance + 200, updatedAt = CURRENT_TIMESTAMP WHERE id = 480001;

-- 2. Marcar transação como paga
UPDATE pix_transactions 
SET status = 'paid', paidAt = CURRENT_TIMESTAMP, updatedAt = CURRENT_TIMESTAMP 
WHERE customerId = 480001 AND amount = 200 AND status = 'pending'
LIMIT 1;

-- 3. Verificar saldo final
SELECT id, email, balance FROM customers WHERE id = 480001;

