SELECT txid, customerId, amount, status FROM pix_transactions WHERE txid = 'ed627307c6434f96b195abe1a3f27a6c';

-- 2. Buscar saldo atual
SELECT id, balance FROM customers WHERE id = 480001;

-- 3. Atualizar saldo (R$ 18,41 + R$ 1,10 = R$ 19,51)
UPDATE customers SET balance = balance + 110 WHERE id = 480001;

-- 4. Atualizar transação PIX
UPDATE pix_transactions SET status = 'completed', paidAt = NOW() WHERE txid = 'ed627307c6434f96b195abe1a3f27a6c';

-- 5. Criar registro de recarga
INSERT INTO recharges (customerId, amount, method, status, createdAt, updatedAt)
VALUES (480001, 110, 'pix', 'completed', NOW(), NOW());

-- 6. Verificar saldo atualizado
SELECT id, balance FROM customers WHERE id = 480001;

